import React from 'react';
import './Menu.css';
import menuItems from '../MenuList/MenuItems';
import MenuList from '../MenuList/MenuList';


class Menu extends React.Component {  
    constructor(props) {
        super(props);
        this.state = {
            menuCategory: 'Appetizers',
        }
        this.handleCategoryChange = this.handleCategoryChange.bind(this);
    }

    handleCategoryChange(e) {
        this.setState({
            menuCategory: e.dataset.id,
        });
    }


    render() {
        return (
            <div className="menu"> 
                <ul className="menu-bar">
                    <li onMouseDown={this.handleCategoryChange} data-id='Appetizers'>Appetizers</li>
                    <li>Soups & Salads</li>
                    <li>Combination Lunch</li>
                    <li>Lunch Favorites</li>
                    <li>Burritos</li>
                    <li>Enchiladas</li>
                    <li>Quesadilas</li>
                    <li>Chimichangas</li>
                    <li>Chicken Dishes</li>
                    <li>Beef and Pork Dishes</li>
                    <li>Kiosco Favorites</li> 
                    <li>Fajitas Mexicanas</li>
                    <li>Combo Dinners</li>
                    <li>Vegetarian Combos</li>
                    <li>Children's Plates</li>
                    <li>Seafood</li>
                    <li>New Plates</li>
                </ul>
                <MenuList category={this.state.menuCategory} />
            </div>  
        )
    }
}

export default Menu;
